#define _CRT_NONSTDC_NO_WARNINGS
#define _CRT_SECURE_NO_WARNINGS
#include <stdio.h>
#include <conio.h>
#include <stdlib.h>#include <fstream>#include <iostream>#include <string>using namespace std;#define MAX 50struct Hocsinh{	int MaHS;	char Hoten[50],Gioitinh[4];	float DiemHK1,DiemHK2;};void xuat1HS(Hocsinh HS);void nhap1HS(Hocsinh &hs);void nhap1HS(Hocsinh &hs){	printf("\nNhap Ma HS: "); 	scanf_s("%d",&hs.MaHS);    printf("\nNhap ten: "); 	fflush(stdin); 	gets(hs.Hoten);    printf("\nNhap gioi tinh: ");	gets(hs.Gioitinh);    printf("\nNhap Diem TB HK1: "); 	scanf("%f", &hs.DiemHK1);	printf("\nNhap Diem TB HK2: "); 	scanf("%f", &hs.DiemHK2);}//nhap nhieu hoc sinhvoid nhapN(Hocsinh a[], int &n){	printf("nhap so hoc sinh:");	scanf_s("%d",&n);    for(int i = 0; i< n; ++i)	{        printf("\nNhap SV thu %d:", i+1);        nhap1HS(a[i]);    }}void xuat1HS(Hocsinh HS){	printf("\n%-10d%-30s%-10s",HS.MaHS,HS.Hoten,HS.Gioitinh);	printf("%-10.2f%.2f\n",HS.DiemHK1,HS.DiemHK2);}void xuatNHS(Hocsinh a[], int n){	printf("MaHS      Ho Ten                       Gioi Tinh   HKI      HKII\n");    for(int i =0; i<n; ++i)	{        xuat1HS(a[i]);    }	}void Menu(){	//system("cls");        printf("******************************************\n");        printf("**    CHUONG QUAN LY HOC SINH     **\n");        printf("**      1. Nhap du lieu                 **\n");        printf("**      2. Doc file                     **\n");        printf("**      3. Them hoc sinh vao cuoi       **\n");        printf("**      4. Them hoc sinh tai vi tri K   **\n");        printf("**      5. Tim Hoc sinh                 **\n");        printf("**      0. Thoat                        **\n");        printf("******************************************\n");}void main(){	Hocsinh a[MAX];	int n,chon;	string s;	do	{		Menu();		printf("\nNhap gia tri de thuc hien:");		scanf_s("%d",&chon);		switch(chon)		{		case 1:			nhapN(a,n);			xuatNHS(a,n);			break;			}	}while(chon!=0);}void load1HS(Hocsinh &x, FILE* f) {	char a[10] = "", b[10] = "",c[10]="";	fscanf(f, "%[^#]%*c%[^#]%*c%[^#]%*c%[^#]%*c%[^\n]%*c", &a, x.Hoten,x.Gioitinh, &b,&c);	x.MaHS = atoi(a);	x.DiemHK1 = atof(b);	x.DiemHK2=atof(c);}void Read(Hocsinh a[],int &n){	FILE*f=fopen("data1.txt","rt");	if(!f)	{		printf("loi file");		return;	}		char b[10]="";		fscanf(f, "%[^\n]%*c", &b);	n = atoi(b);	for (int i = 0; i < n; i++)		load1HS(a[i], f);	fclose(f);}